import { Component, OnInit } from '@angular/core';
import { HomeDefaultComponent } from 'src/app/components/home-default/home-default.component';
import { AuthService } from 'src/app/services/auth-service';

@Component({
  selector: 'app-user-auth',
  standalone: true,
  imports: [HomeDefaultComponent],
  templateUrl: './home.component.html',
  styleUrl: './home.component.scss'
})
export class HomeComponent implements OnInit {
  usernameInitials: string = '';

  constructor(private authService: AuthService) { }

  ngOnInit(): void {
    const username = this.authService.getUsername();
    if (username) {
      this.usernameInitials = this.getUserInitials(username);
    }
  }

  private getUserInitials(username: string): string {
    const names = username.split(' ');
    if (names.length >= 2) {
      return `${names[0].charAt(0)} ${names[names.length - 1].charAt(0)}`.toUpperCase();
    } else if (names.length === 1) {
      return `${names[0].charAt(0)}`.toUpperCase();
    }
    return '';
  }
}
