import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { UserService } from 'src/app/services/user-service';

@Component({
  selector: 'app-notification-navbar',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './notification-navbar.component.html',
  styleUrl: './notification-navbar.component.scss'
})
export class NotificationNavbarComponent {
  userSubStatus: string = '';

  constructor(
    private userService: UserService
  ) { }

  ngOnInit(): void {
    const userId = sessionStorage.getItem('userId');
  
    if (userId) {
      this.userService.getUserInfo(userId).subscribe(
        (user) => {
          this.userSubStatus =  `${user.subStatus}`;
        },
        (error) => {
          console.error('Erro ao obter informações do usuário', error);
        }
      );
    } else {
      console.error('ID do usuário não encontrado na sessão');
    }
  }

}
<div class="box-notifications">
  <div class="notifications">
    <div class="title-notifications">
      <span>Notifications</span>
    </div>

    <div class="container-notifications"  *ngIf="userSubStatus !== 'UNLOCKED'"><span>You have no notifications.</span></div>

  </div>
</div>
