import { CommonModule } from '@angular/common';
import { Component, EventEmitter, Input, Output} from '@angular/core';
import { UserService } from 'src/app/services/user-service';

@Component({
  selector: 'app-header-profile',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './header-profile.component.html',
  styleUrl: './header-profile.component.scss'
})
export class HeaderProfileComponent {
  userInitials: string = ''; 
  userFullName: string = '';
  userEmail: string = '';
  userRole: string = '';
  userStatus: string = '';
  userIdentity: string = '';
  @Input() disablePrimaryBtn: boolean = true;
  @Output("submit") onSubmit = new EventEmitter();
  @Output("navigate") onNavigate = new EventEmitter();

  constructor(
    private userService: UserService
  ) { }

  ngOnInit(): void {
    const userId = sessionStorage.getItem('userId');
  
    if (userId) {
      this.userService.getUserInfo(userId).subscribe(
        (user) => {
          this.userInitials = `${user.first_name.charAt(0)} ${user.last_name.charAt(0)}`;
          this.userFullName = `${user.first_name} ${user.last_name}`;
          this.userEmail =  `${user.email}`;
          this.userRole =  `${user.role}`;
          this.userStatus =  `${user.status}`;
          this.userIdentity = `${user.identity}`;
        },
        (error) => {
          console.error('Erro ao obter informações do usuário', error);
        }
      );
    } else {
      console.error('ID do usuário não encontrado na sessão');
    }
  }

  submit(){
    this.onSubmit.emit();
  }

  navigate(){
    this.onNavigate.emit();
  }


}
<div class="header-profile">
  <div class="div-control-profile">
    <div class="photo-section">
      <div
        [ngClass]="{
          available: userStatus === 'AVAILABLE',
          unavailable: userStatus === 'UNAVAILABLE'
        }"
        class="profile-user"
      >
        <span>{{ userInitials }}</span>
      </div>

      <div class="status-user">
        {{ userStatus }}
      </div>
    </div>

    <div class="infos-section">
      <div class="infos-basics">
        <div class="name">
          <span>Name</span>
          <span class="info-value">
            Luana Alves Nogueira dos Santos Pereira</span
          >
        </div>

        <div class="email">
          <span>Email</span>
          <span class="info-value">{{ userEmail }}</span>
        </div>

        <div class="identity">
          <span>Identity (ID)</span>
          <span class="info-value">{{ userIdentity }}</span>
        </div>
      </div>

      <div class="btns-section">
        <button
          class="btn-primary"
          (click)="submit()"
          [disabled]="disablePrimaryBtn"
          *ngIf="userRole === 'USER'"
        >
          <img src="/assets/svg/header-profile/alert.svg" /> Create Alert
        </button>

        <button
          class="btn-primary"
          (click)="submit()"
          [disabled]="disablePrimaryBtn"
          *ngIf="userRole === 'USER'"
        >
          <img src="/assets/svg/header-profile/add.svg" /> Register User
        </button>

        <button
          class="btn-primary"
          (click)="submit()"
          [disabled]="disablePrimaryBtn"
          *ngIf="userRole === 'USER'"
        >
          <img src="/assets/svg/header-profile/view.svg" /> View Profile
        </button>
      </div>
    </div>
  </div>
</div>
