import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { SidebarComponent } from '../sidebar/sidebar.component';
import { AuthService } from 'src/app/services/auth-service';
import { LogoutService } from 'src/app/services/logout-service';

@Component({
  selector: 'app-home-default',
  standalone: true,
  imports: [CommonModule, SidebarComponent],
  templateUrl: './home-default.component.html',
  styleUrl: './home-default.component.scss',
})
export class HomeDefaultComponent {
  userInitials: string = ''; // Variável para armazenar as iniciais do usuário

  constructor(private authService: AuthService, private logoutService: LogoutService) {}

  ngOnInit(): void {
    const firstName = this.authService.getFirstName(); // Método para obter o primeiro nome do AuthService
    const lastName = this.authService.getLastName(); // Método para obter o sobrenome do AuthService

    // Lógica para obter as iniciais do primeiro e último nome
    if (firstName && lastName) {
      this.userInitials = `${firstName.charAt(0)} ${lastName.charAt(0)}`;
    } else {
      this.userInitials = 'N/A'; // Caso não seja possível obter os nomes, você pode definir um valor padrão
    }
  }

  isSubMenuOpen: boolean = false;

  toggleSettingsMenu() {
    this.isSubMenuOpen = !this.isSubMenuOpen;
  }

  logout(): void {
    this.logoutService.logout();
  }
}
