import { CommonModule } from '@angular/common';
import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-success-modal',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './success-modal.component.html',
  styleUrl: './success-modal.component.scss'
})
export class SuccessModalComponent {
  @Input() registeredEmail: string = '';
  @Input() isOpen: boolean = false;

  closeModal() {
    this.isOpen = false;
  }

}
<div *ngIf="isOpen" class="custom-modal">
  <div class="modal-content">
    <div class="header-modal">
      <div class="title-modal">User Registration Successfully!</div>
      <div class="close" (click)="closeModal()">&times;</div>
    </div>
    <div class="section-content">
      <div class="icon-success">
        <img src="/assets/svg/utils/success.svg" />
      </div>
      <div class="primary-message">
        The user with email <span>{{ registeredEmail }}</span> was
        registered in our system.
      </div>
    </div>
    <div class="secondary-message">
      <span
        >To obtain your access <b>ID</b> and <b>password</b>, check the inbox
        and spam folder of the email you used to register.
      </span>
    </div>
    <div class="div-btn">
    <button
      class="btn-primary"
      (click)="closeModal()"
    >
      close
    </button>
  </div></div>
</div>
