  //   // Realizar o login
  //   this.loginService.login(identity, password).subscribe({
  //     next: (loginResponse) => {
  //       const userId = loginResponse.userId;
  //       console.log('ID TENTANDO LOGIN' + userId);

  //       // Verificar o substatus do usuário após o login
  //       this.userService.getUserById(userId).subscribe({
  //         next: (user: { subStatus: string }) => {
  //           if (user.subStatus === 'BLOCKED') {
  //             this.toastService.error('Your account is blocked!');
  //             console.log('BLOQUEADO');
  //             // Opcional: Deslogar o usuário ou tomar alguma ação adicional
  //           } else {
  //             this.toastService.success('Login successfully!');
  //             this.router.navigate(['user']);
  //           }
  //         },
  //         error: () =>
  //           this.toastService.error('Unexpected error! Try again later'),
  //       });
  //     },
  //     error: () => this.toastService.error('Unexpected error! Try again later'),
  //   });
  // }

  navigate() {
    this.router.navigate(['signup']);
  }

  private setNotificationMessage(): void {
    switch (this.userSubStatus) {
      case 'BLOCKED':
        console.log('BLOQUEADO');
        break;
      default:
        break;
    }
  }
}
