import { CommonModule } from '@angular/common';
import { Component, Input, EventEmitter, Output  } from '@angular/core';
import { ModalDefaultComponent } from '../../../components/modal-default/modal-default.component';

@Component({
  selector: 'app-successfully-email-modal',
  standalone: true,
  imports: [CommonModule, ModalDefaultComponent],
  templateUrl: './email-successfully-sent-modal.html',
})
export class EmailSuccessfullySentModal {
  @Input() registeredEmail: string = '';
  @Input() isOpen: boolean = false;
  @Output() onCloseModal = new EventEmitter<void>();

  closeModal() {
    this.onCloseModal.emit();
  }

}
<app-modal-default
  [headerBackgroundColor]="'#BCE4A9'"
  [headerTextColor]="'#5BAF33'"
  [closeTextColor]="'#5BAF33'"
  titleModal="Email was sent successfully!"
  srcIcon="/assets/svg/utils/success.svg"
  primaryMessageText=" The email with your new password has been sent to the following email address: {{ registeredEmail }} "
  secondaryMessageText="Check your inbox or spam box, if not, contact support peoplehuba.admin@peoplehub.com"
  [borderColorPrimaryBtn]="'1px solid #5BAF33'"
  [colorTextPrimaryBtn]="'#5BAF33'"
  primaryBtnText="cancel"
  secondaryBtnText=""
  (closeModal)="closeModal()"
  *ngIf="isOpen"
></app-modal-default>
