import { Component} from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { DefaultLoginLayoutComponent } from 'src/app/components/default-login-layout/default-login-layout.component';
import { Router } from '@angular/router';
import { InputComponent } from 'src/app/components/input/input.component';

interface SignupForm {
  first_name: FormControl,
  last_name: FormControl,
  email: FormControl,
  role: FormControl,
}

@Component({
  selector: 'app-signup',
  standalone: true,
  imports: [
    DefaultLoginLayoutComponent,ReactiveFormsModule, InputComponent,
  ],
  providers:[

  ],
 templateUrl: './signup.component.html',
  styleUrl: './signup.component.scss'
})
export class SignupComponent {
  signupForm!: FormGroup<SignupForm>;

  constructor(
    private router: Router,
  ){
    this.signupForm = new FormGroup({
      first_name: new FormControl('', [Validators.required, Validators.minLength(3)]),
      last_name: new FormControl('', [Validators.required, Validators.minLength(3)]),
      email: new FormControl('', [Validators.required, Validators.email]),
      role: new FormControl('', [Validators.required, Validators.minLength(4)]),
    })
  }

  submit(){
  
  }

  navigate(){
    this.router.navigate(["login"])
  }

}
