import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth-service';
import { LogoutService } from 'src/app/services/logout-service';
import { UserService } from 'src/app/services/user-service';

@Component({
  selector: 'app-submenu-navbar',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './submenu-navbar.component.html',
  styleUrl: './submenu-navbar.component.scss'
})
export class SubmenuNavbarComponent {
  isSubMenuOpen: boolean = false;
  userStatus: string = '';

  constructor(
    private authService: AuthService,
    private userService: UserService
  ) { }


  toggleSettingsMenu() {
    console.log('Settings menu toggled');
    this.isSubMenuOpen = !this.isSubMenuOpen;
  }

  ngOnInit(): void {
    const userId = sessionStorage.getItem('userId');
  
  if (userId) {
    this.userService.getUserInfo(userId).subscribe(
      (user) => {
        this.userStatus =  `${user.status}`;
      },
      (error) => {
        console.error('Erro ao obter informações do usuário', error);
      }
    );
  } else {
    console.error('ID do usuário não encontrado na sessão');
  }
}

}
<div
  class="settings-submenu"
  (mouseleave)="toggleSettingsMenu()"
>
  <div class="title-submenu">
    <img src="/assets/svg/submenu-navbar/configurations.svg" /><span>Settings</span>
  </div>
  
  <div class="div-control-status">
    <div 
      [ngClass]="{'available': userStatus === 'AVAILABLE', 'unavailable': userStatus === 'UNAVAILABLE'}" 
      class="status-user" 
      style="width:12px; height:12px;"
    >
      <span>{{ userStatus }}</span>
    </div>
  </div>

  <a href="#link1">
    <img src="/assets/svg/submenu-navbar/profile.svg" /> My profile
  </a>

  <a href="#link2">
    <img src="/assets/svg/submenu-navbar/password-lock.svg" />Password Management
  </a>

  <button class="close-btn" (click)="toggleSettingsMenu()">X</button>
</div>
