import { Component } from '@angular/core';

@Component({
  selector: 'app-sidebar',
  standalone: true,
  imports: [],
  templateUrl: './sidebar.component.html',
  styleUrl: './sidebar.component.scss'
})
export class SidebarComponent {
  isSidebarOpen: boolean = true;  
  
  toggleSidebar() {
    this.isSidebarOpen = !this.isSidebarOpen;
  }
}
<div [class.sidebar-closed]="!isSidebarOpen" class="sidebar">

    <div class="control-sidebar">
      <img (click)="toggleSidebar()" class="icon-close-open" style="cursor: pointer" src="/assets/svg/menu-sidebar/open-close-sidebar.svg" />
    </div>
      
      <div class="search-container">
        <img
          src="/assets/svg/menu-sidebar/search.svg"
          alt="Search Icon"
          class="search-icon"
        />
        <input type="text" placeholder="search" class="search-input" />
      </div>
      
      <div class="sidebar-link">
        <a href="#link2"
              ><img src="/assets/svg/menu-sidebar/home.svg" /> <span>Dashboard</span></a>
        
    </div>

  </div>
