import { Component } from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { InputComponent } from 'src/app/components/input/input.component';
import { Router } from '@angular/router';
import { LoginService } from 'src/app/services/login-service';
import { ToastrService } from 'ngx-toastr';
import { identityValidator } from '../../validators/validador-indentity';
import { LoginLayoutComponent } from 'src/app/components/login-layout-component/login-layout.component';
import { ModalRecoveryPasswordComponent } from 'src/app/pages/recovery-password/modal-recovery-password.component';
import { UserService } from 'src/app/services/user-service';

interface LoginForm {
  identity: FormControl,
  password: FormControl
}

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [
    ReactiveFormsModule,
    InputComponent,
    LoginLayoutComponent,
    ModalRecoveryPasswordComponent
  ],
  providers:[
    LoginService
  ],
 templateUrl: './login.component.html',
  styleUrl: './login.component.scss'
})
export class LoginComponent {
  loginForm!: FormGroup<LoginForm>;
  userSubStatus: string = '';

  constructor(
    private router: Router,
    private loginService: LoginService,
    private toastService: ToastrService,
    private userService: UserService,
  ){
    this.loginForm = new FormGroup({
      identity: new FormControl('', [Validators.required, identityValidator()]),
      password: new FormControl('', [Validators.required, Validators.minLength(6)])
    })
  }



  submit() {
    const identity = this.loginForm.value.identity;
    const password = this.loginForm.value.password;

    // Realizar o login
    this.loginService.login(identity, password).subscribe({
      next: (loginResponse) => {
        const userId = loginResponse.userId;

        // Verificar o substatus do usuário após o login
        this.userService.getUserById(userId).subscribe({
          next: (user: { subStatus: string; }) => {
            if (user.subStatus === 'BLOCKED') {
              this.toastService.error('Your account is blocked!');
              console.log("BLOQUEADO")
              // Opcional: Deslogar o usuário ou tomar alguma ação adicional
            } else {
              this.toastService.success("Login successfully!");
              this.router.navigate(['user']);
            }
          },
          error: () => this.toastService.error('Unexpected error! Try again later')
        });
      },
      error: () => this.toastService.error("Unexpected error! Try again later")
    });
  }


  navigate(){
    this.router.navigate(["signup"])
  }



}
