import { CommonModule } from '@angular/common';
import { Component, Input, EventEmitter, Output  } from '@angular/core';
import { ModalDefaultComponent } from '../modal-default/modal-default.component';

@Component({
  selector: 'app-success-modal',
  standalone: true,
  imports: [CommonModule, ModalDefaultComponent],
  templateUrl: './success-modal.component.html',
  styleUrl: './success-modal.component.scss'
})
export class SuccessModalComponent {
  @Input() registeredEmail: string = '';
  @Input() isOpen: boolean = false;
  @Output() onCloseModal = new EventEmitter<void>();

  closeModal() {
    this.onCloseModal.emit();
  }

}
<app-modal-default
  titleModal="User Registration Successfully!"
  primaryMessageText=" The user with email {{ registeredEmail }} was
  registered in our system."
  secondaryMessageText="To obtain your access ID and password, check the inbox
  and spam folder of the email you used to register."
  primaryBtnText=""
  secondaryBtnText="cancel"
  (onCloseModal)="closeModal()"
  *ngIf="isOpen"
></app-modal-default>
