import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { SidebarComponent } from '../sidebar/sidebar.component';
import { AuthService } from 'src/app/services/auth-service';
import { LogoutService } from 'src/app/services/logout-service';
import { UserService } from 'src/app/services/user-service';
import { NavbarComponent } from '../navbar/navbar.component';

@Component({
  selector: 'app-home-default',
  standalone: true,
  imports: [CommonModule, SidebarComponent, NavbarComponent],
  templateUrl: './home-default.component.html',
  styleUrls: ['./home-default.component.scss'], // Corrigido aqui
})
export class HomeDefaultComponent {
  userInitials: string = ''; 
  userFullName: string = '';
  userEmail: string = '';
  userRole: string = '';
  userStatus: string = '';


  constructor(
    private logoutService: LogoutService,
    private userService: UserService
  ) { }

  ngOnInit(): void {
    const userId = sessionStorage.getItem('userId');
  
    if (userId) {
      this.userService.getUserInfo(userId).subscribe(
        (user) => {
          this.userInitials = `${user.first_name.charAt(0)} ${user.last_name.charAt(0)}`;
          this.userFullName = `${user.first_name} ${user.last_name}`;
          this.userEmail =  `${user.email}`;
          this.userRole =  `${user.role}`;
          this.userStatus =  `${user.status}`;
        },
        (error) => {
          console.error('Erro ao obter informações do usuário', error);
        }
      );
    } else {
      console.error('ID do usuário não encontrado na sessão');
    }
  }

  logout(): void {
    this.logoutService.logout();
  }
}
<main>
  <app-navbar></app-navbar>

  <div class="main-container">
    <app-sidebar></app-sidebar>

    <div class="content">
      <div class="header-profile">

        <div class="div-control-profile">

          <div 
          [ngClass]="{'available': userStatus === 'AVAILABLE', 'UNAVAILABLE': userStatus === 'UNAVAILABLE'}" 
          class="profile-user" 
        >
          <span>{{ userInitials }}</span>
        </div>

        <div class="infos-basics">
          <div class="name">
            <span>Name</span>
            <span style="font-weight: 400; text-transform: capitalize; color:#939393;">{{userFullName}}</span>
          </div>

          <div class="email">
            <span>Email</span>
            <span style="font-weight: 400; text-transform: capitalize; color:#939393;">{{userEmail}}</span>
          </div>

          <div class="role">
            <span>Role</span>
            <span style="font-weight: 400; text-transform: capitalize; color:#939393;">{{userRole}}</span>
          </div>
        </div>

      </div>
        <ng-content></ng-content>
      </div>
     
    </div>
  </div>
</main>
