import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { SidebarComponent } from '../sidebar/sidebar.component';
import { AuthService } from 'src/app/services/auth-service';
import { LogoutService } from 'src/app/services/logout-service';
import { UserService } from 'src/app/services/user-service';

@Component({
  selector: 'app-home-default',
  standalone: true,
  imports: [CommonModule, SidebarComponent],
  templateUrl: './home-default.component.html',
  styleUrl: './home-default.component.scss',
})
export class HomeDefaultComponent {
    userInitials: string = ''; 
    userFullName: string = '';
    userEmail: string = '';
    userRole: string = '';
    userStatus: string = '';

    constructor(
      private authService: AuthService,
      private logoutService: LogoutService,
      private userService: UserService
    ) { }


    ngOnInit(): void {
        const userId = sessionStorage.getItem('userId');
      
      if (userId) {
        this.userService.getUserInfo(userId).subscribe(
          (user) => {
            this.userInitials = `${user.first_name.charAt(0)} ${user.last_name.charAt(0)}`;
            this.userFullName = `${user.first_name} ${user.last_name}`;
            this.userEmail =  `${user.email}`;
            this.userRole =  `${user.role}`;
            this.userStatus =  `${user.status}`;
          },
          (error) => {
            console.error('Erro ao obter informações do usuário', error);
          }
        );
      } else {
        console.error('ID do usuário não encontrado na sessão');
      }
    }

  isSubMenuOpen: boolean = false;

  toggleSettingsMenu() {
    this.isSubMenuOpen = !this.isSubMenuOpen;
  }

  logout(): void {
    this.logoutService.logout();
  }
}
<main>
  <nav class="navbar">
    <div class="div-control-logo">
      <div class="logo-text">
        <span>People</span>
        <span style="font-weight: 600">Hub</span>
      </div>
    </div>

    <div class="div-control-box-infos">
      <div class="box-infos">
        <img
          style="cursor: pointer"
          src="/assets/svg/navbar/notification.svg"
        />

        <img
          style="cursor: pointer"
          src="/assets/svg/navbar/settings.svg"
          (click)="toggleSettingsMenu()"
        />

        <div
          class="settings-submenu"
          *ngIf="isSubMenuOpen"
          (mouseleave)="toggleSettingsMenu()"
        >
          <div class="title-submenu">
            <img src="/assets/svg/submenu-navbar/configurations.svg" /><span
              >Settings</span
            >
          </div>
          
          <div 
          [ngClass]="{'available': userStatus === 'AVAILABLE', 'UNAVAILABLE': userStatus === 'UNAVAILABLE'}" 
          class="status-user" 
          style="width:10px; height:10px;"
        >
          <span>{{ userStatus }}</span>
        </div>

          <a href="#link1">
            <img src="/assets/svg/submenu-navbar/profile.svg" /> My profile</a
          >

          <a href="#link2"
            ><img src="/assets/svg/submenu-navbar/password-lock.svg" />Password
            Management</a
          >

          <button class="close-btn" (click)="toggleSettingsMenu()">X</button>
        </div>

        <div class="user-name">
          <span>{{userInitials}}</span>
        </div>
      </div>

      <div class="icon-logout">
        <img style="cursor: pointer" src="/assets/svg/navbar/logout.svg" (click)="logout()" />
      </div>
    </div>
  </nav>

  <div class="main-container">
    <app-sidebar></app-sidebar>

    <div class="content">
      <div class="header-profile">

        <div class="div-control-profile">

          <div 
          [ngClass]="{'available': userStatus === 'AVAILABLE', 'UNAVAILABLE': userStatus === 'UNAVAILABLE'}" 
          class="user-name" 
          style="width:120px; height:120px;"
        >
          <span>{{ userInitials }}</span>
        </div>

        <div class="infos-basics">
          <div class="name">
            <span>Name</span>
            <span style="font-weight: 400; text-transform: capitalize; color:#939393;">{{userFullName}}</span>
          </div>

          <div class="email">
            <span>Email</span>
            <span style="font-weight: 400; text-transform: capitalize; color:#939393;">{{userEmail}}</span>
          </div>

          <div class="role">
            <span>Role</span>
            <span style="font-weight: 400; text-transform: capitalize; color:#939393;">{{userRole}}</span>
          </div>
        </div>

      </div>
        <ng-content></ng-content>
      </div>
     
    </div>
  </div>
</main>
